var SpacingModule = (function () {
    var globalSpacing = {};
    var moduleSpacing = {
        "name": "spacingModule",
        "selector": "body",
        "components": {
            "all": {
                "top": {
                    "transition": "20 30 40"
                },
                "bottom": {
                    "transition": "20 30 40",
                    "contextual-links-container": "0 0 0"
                }
            },
            "contextual-links-container": {
                "top": {
                    "all": "0 0 0"
                },
                "bottom": {
                    "all": "0 0 0"
                }
            },
            "contextual-links-cont-home" : {
                "top": {
                    "first": "20 30 40"
                },
                "bottom": {}
            },
            "contextual-links-home-us" : {
                "top": {
                    "first": "20 30 40"
                },
                "bottom": {}
            },
            "glossary-container": {
                "top": {
                    "first": "0 0 0"
                },
                "bottom": {}
            }
        }
    };

    // Adjusts the spacing
    function adjustSpacing(moduleSpacing) {
        var components, length;
        var selector, spacing;

        // add spacing object to global
        if (!globalSpacing.hasOwnProperty(moduleSpacing["name"])) {
            globalSpacing[moduleSpacing["name"]] = moduleSpacing;
        }

        // spacing solution for each subset
        $.each(globalSpacing, function (spacingName, spacingObject) {
            //console.log(spacingName);
            selector = spacingObject["selector"];
            spacing = spacingObject["components"];

            $(selector).each(function () {
                // select components
                components = $(this).find("[data-comp-spacing]").filter(function () {
                    return !$(this).parentsUntil(selector).is("[data-comp-spacing]");
                });

                // reset spacing
                components.removeClass(function (index, className) {
                    var classMatch = (className.match(/(^|\s)(pad-t-|pad-b-)\S+/g) || []).join(' ');
                    //console.log(classMatch);
                    return classMatch;
                });
                //console.log("selector: " + selector);
                //console.log(components);

                length = components.length;

                for (var index = 0; index < length; index++) {
                    var comp1, comp2, prop1, prop2;
                    comp1 = components.eq(index);
                    prop1 = comp1.attr("data-comp-spacing");

                    // Apply TOP spacing to first component
                    if (index == 0) {
                        if (spacing.hasOwnProperty(prop1)) {
                            if (spacing[prop1].top.hasOwnProperty("first")) {
                                comp1.addClass(classFormat(spacing[prop1].top["first"], "top"));
                            }
                        } else if (spacing.hasOwnProperty("all")) {
                            if (spacing["all"].top.hasOwnProperty("transition") && comp1.css("background-color") == "rgb(255, 255, 255)") {
                                comp1.addClass(classFormat(spacing["all"].top["transition"], "top"));
                            } else if (spacing["all"].top.hasOwnProperty("first")) {
                                comp1.addClass(classFormat(spacing["all"].top["first"], "top"));
                            }
                        }
                    }

                    // Apply spacing to in between components (BOTTOM/TOP)
                    if (index < length - 1) {
                        comp2 = components.eq(index + 1);
                        prop2 = comp2.attr("data-comp-spacing");

                        if (spacing.hasOwnProperty(prop1)) {
                            if (spacing[prop1].bottom.hasOwnProperty(prop2)) {
                                comp1.addClass(classFormat(spacing[prop1].bottom[prop2], "bottom"));
                            } else if (spacing[prop1].bottom.hasOwnProperty("all")) {
                                comp1.addClass(classFormat(spacing[prop1].bottom["all"], "bottom"));
                            }
                        } else if (spacing.hasOwnProperty("all")) {
                            if (spacing["all"].bottom.hasOwnProperty("transition") && (comp1.css("background-color") != comp2.css("background-color"))) {
                                comp1.addClass(classFormat(spacing["all"].bottom["transition"], "bottom"));
                            } else if (spacing["all"].bottom.hasOwnProperty(prop2)) {
                                comp1.addClass(classFormat(spacing["all"].bottom[prop2], "bottom"));
                            }
                        }

                        if (spacing.hasOwnProperty(prop2)) {
                            if (spacing[prop2].top.hasOwnProperty(prop1)) {
                                comp2.addClass(classFormat(spacing[prop2].top[prop1], "top"));
                            } else if (spacing[prop2].top.hasOwnProperty("all")) {
                                comp2.addClass(classFormat(spacing[prop2].top["all"], "top"));
                            }
                        } else if (spacing.hasOwnProperty("all")) {
                            if (spacing["all"].top.hasOwnProperty("transition") && (comp1.css("background-color") != comp2.css("background-color"))) {
                                comp2.addClass(classFormat(spacing["all"].top["transition"], "top"));
                            } else if (spacing["all"].top.hasOwnProperty(prop1)) {
                                comp2.addClass(classFormat(spacing["all"].top[prop1], "top"));
                            }
                        }
                    }

                    // Apply BOTTOM spacing to last component
                    if (index == length - 1) {
                        if (spacing.hasOwnProperty(prop1)) {
                            if (spacing[prop1].bottom.hasOwnProperty("last")) {
                                comp1.addClass(classFormat(spacing[prop1].bottom["last"], "bottom"));
                            }
                        } else if (spacing.hasOwnProperty("all")) {
                            if (spacing["all"].bottom.hasOwnProperty("last")) {
                                comp1.addClass(classFormat(spacing["all"].bottom["last"], "bottom"));
                            }
                        }
                    }
                }
            });
        });
    }

    // Formats the CSS class names
    function classFormat(val, pos) {
        var string = "";
        if (val != "") {
            var pad = val.split(" ");
            if (pad.length == 3) {
                if (pos == "top") {
                    string += "pad-t-sm-" + pad[0] + " pad-t-md-" + pad[1] + " pad-t-lg-" + pad[2];
                } else if (pos == "bottom") {
                    string += "pad-b-sm-" + pad[0] + " pad-b-md-" + pad[1] + " pad-b-lg-" + pad[2];
                }
            }
        }
        return string;
    }

    return {
        init: function () {
            adjustSpacing(moduleSpacing);
            $(window).on("resize", function () {
                if (UtilityModule.viewportChange()) {
                    adjustSpacing(moduleSpacing);
                }
            });
        },
        adjustSpacing: adjustSpacing
    };
})();
SpacingModule.init();
