var bootPagNum = 0;
var listCount = 10;
var maxPagCount = 5;
var count = 0;

var PaginationModule = (function () {
    function createPagination(result) {
        $('.results_content').children().removeClass('.hidden');
        var notHiddenList = $(".results_content").children().not('.hidden');
        var listLength = result;
        var st_cnt = 0;
        var end_cnt = 0;
        // Setting listLength to 0 manually when only undefined are returned
        if (typeof result != 'undefined') {
            if (result.count == 0)
                listLength = 0;
        }
        if (result < listCount) {
            $('.results_pagination').addClass('hidden');
            $(".results_content").children().removeClass('hidden');
            st_cnt = 1;
            end_cnt = listCount;
        }
        else {
            st_cnt = 1;
            end_cnt = listCount;
            $('.results_pagination').removeClass('hidden');
            $(".results_content").children().addClass('hidden');
            $(".results_content").children(':lt(' + listCount + ')').removeClass('hidden');
            $('.results_pagination').bootpag({
                total: Math.ceil(listLength / listCount),
                page: 1,
                maxVisible: maxPagCount,
                leaps: false,
                firstLastUse: true,
                first: "&#10094;&#10094;",
                prev: "&#10094",
                last: "&#10095;&#10095;",
                next: "&#10095;"
            }).on("page", function (event, num) {
                $(".results_content").children().addClass('hidden');
                if (num == 1) {
                    $(".results_content").children(':lt(' + listCount + ')').removeClass('hidden');
                    st_cnt = 1;
                    end_cnt = listCount;
                }
                else {
                    var start = (listCount * (num - 1)) - 1;
                    var end = listCount;
                    $(".results_content").children(':gt(' + start + '):lt(' + end + ')').removeClass('hidden');
                    st_cnt = listCount * (num - 1) + 1;
                    end_cnt = listCount * num;
                }
                if (end_cnt > listLength) {
                    end_cnt = listLength;
                }
                $('.display-text > span:first-of-type').html(st_cnt + '&nbsp;' + 'of' + '&nbsp;' + end_cnt);
                // ... after content load -> change total to 10
                $('.results_pagination').bootpag({
                    total: Math.ceil(count / listCount),
                    maxVisible: maxPagCount
                });
            });
        }

        if (count == 0) {
            st_cnt = listLength;
            end_cnt = listLength;
            $('.display-text > span:nth-of-type(2)').addClass('hidden');
            $('.results_pagination').addClass('hidden');
        }
        else {
            $('.display-text > span:nth-of-type(2)').removeClass('hidden');
            $('.display-text > span:nth-of-type(2)').html('&nbsp;' + count);
        }
        if (end_cnt < result) {
            $('.display-text > span:first-of-type').html(st_cnt + '&nbsp;' + '-' + '&nbsp;' + end_cnt);
        } else {
            $('.display-text > span:first-of-type').html(st_cnt + '&nbsp;' + '-' + '&nbsp;' + result);
        }
    }

    function removePagination() {
        count = 0;
        PaginationModule.createPagination(count);
        $(".results_content").remove();
        $(".results_pagination").addClass("hidden");
    }

    return {
        createPagination: createPagination,
        removePagination: removePagination
    };
})();

var AccordionModule = (function () {
    function getParent(expandCollapseButton) {
        var parent;
        if (expandCollapseButton.closest(".list").length > 0) {
            parent = expandCollapseButton.closest(".list").find(".accordion");
        } else {
            parent = expandCollapseButton.closest(".accordion");
        }
        return parent;
    }

    function expandSingle(item, transition) {
        var title = item;
        var content = item.siblings(".accordion__content");
        if (!content.is(':visible')) {
            if (transition == "slow") {
                content.slideDown('slow').scrollTop(0);
            } else {
                content.show();
            }
            title.find('.icon-plus').hide();
            title.find('.icon-minus').show();
        }
    }

    function expandAll(parent, transition) {
        parent.find(".expandAll").hide();
        parent.find(".collapseAll").show();
        parent.find(".accordion__title").each(function () {
            expandSingle($(this), transition);
        });
    }

    function collapseSingle(item, transition) {
        var title = item;
        var content = item.siblings(".accordion__content");
        if (content.is(':visible')) {
            if (transition == "slow") {
                content.slideUp('slow').scrollTop(0);
            } else {
                content.hide();
            }
            title.find('.icon-plus').show();
            title.find('.icon-minus').hide();
        }
    }

    function collapseAll(parent, transition) {
        parent.find(".expandAll").show();
        parent.find(".collapseAll").hide();
        parent.find(".accordion__title").each(function () {
            collapseSingle($(this), transition);
        });
    }

    return {
        init: function () {
            // Expand All Accordions
            $(".accordion .expandAll").on("click", function () {
                var parent = getParent($(this));
                expandAll(parent, "slow");
            });

            // Collapse All Accordions
            $(".accordion .collapseAll").on("click", function () {
                var parent = getParent($(this));
                collapseAll(parent, "slow");
            });

            // Expand/Collapse Each Accordion
            $(".accordion .accordion__title").on("click", function () {
                var parent = getParent($(this));

                // Close others upon new open
                if (!$(this).siblings(".accordion__content").is(':visible')) {
                    collapseAll(parent, "slow");
                    expandSingle($(this), "slow");
                } else {
                    parent.find(".expandAll").show();
                    parent.find(".collapseAll").hide();
                    collapseSingle($(this), "slow");
                }
            });
        },
        collapseAll: collapseAll
    };
})();
AccordionModule.init();

