                // index.
                if (!ss_qbackup) {
                    ss_qbackup = fo.query.value;
                }
                ss_loc = ri - 1;
                // Find out what type of suggestion it is.
                var suggestion = ss_locateSuggestion(ss_qbackup, ss_loc);
