var GenericTableModule = (function () {
    var tableColumns = 3;

    // Initial Formatting of Rate Table
    function formatRateTable() {
        $(".rate_table").each(function () {
            var parent = $(this);
            var bodyContent, bodyLocation;
            var i, j;
            if (parent.hasClass("rate_table--variation-1")) {
                // appends the body content and data-target class
                bodyLocation = parent.find(".content_body--variation .content_table");
                for (i = 0; i < parent.find(".content_temp tbody tr").length; i++) {
                    bodyLocation.append("<tr></tr>");
                    for (j = 0; j < parent.find(".content_temp tr:first-child td").length; j++) {
                        bodyContent = parent.find(".content_temp tr").eq(i).children("td").eq(j).text();
                        switch (j) {
                            case 0:
                                bodyLocation.find("tr").eq(i + 1).append("<td class='sticky-left-column health-class-" + j + "'>" + bodyContent + "</td>");
                                break;
                            case 1:
                                bodyLocation.find("tr").eq(i + 1).append("<td class='active health-class-" + j + "'>" + bodyContent + "</td>");
                                break;
                            default:
                                bodyLocation.find("tr").eq(i + 1).append("<td class='health-class-" + j + "'>" + bodyContent + "</td>");
                        }
                    }
                }
            } else if (parent.hasClass("rate_table--variation-2")) {
                // appends the body content and data-target class
                bodyLocation = parent.find(".content_body--variation .content_table");
                for (i = 0; i < parent.find(".content_temp tbody tr").length; i++) {
                    bodyLocation.append("<tr></tr>");
                    for (j = 0; j < parent.find(".content_temp tr:first-child td").length; j++) {
                        bodyContent = parent.find(".content_temp tr").eq(i).children("td").eq(j).text();
                        switch (j) {
                            case 0:
                                bodyLocation.find("tr").eq(i + 1).append("<td class='sticky-left-column health-class-" + j + "'>" + bodyContent + "</td>");
                                break;
                            case 1:
                                bodyLocation.find("tr").eq(i + 1).append("<td class='active health-class-" + j + "'>" + bodyContent + "</td>");
                                j++;
                                bodyContent = parent.find(".content_temp tr").eq(i).children("td").eq(j).text();
                                bodyLocation.find("tr").eq(i + 1).append("<td class='active health-class-" + (j - 1) + "'>" + bodyContent + "</td>");
                                break;
                            default:
                                bodyLocation.find("tr").eq(i + 1).append("<td class='health-class-" + ((j + 1) / 2) + "'>" + bodyContent + "</td>");
                                j++;
                                bodyContent = parent.find(".content_temp tr").eq(i).children("td").eq(j).text();
                                bodyLocation.find("tr").eq(i + 1).append("<td class='health-class-" + (j / 2) + "'>" + bodyContent + "</td>");
                        }
                    }
                }
            } else {
                if (parent.parent().hasClass("two-column-table")) {
                    // removes optional components
                    parent.find(".content_corner, .content_top, .content_left").remove();
                    parent.find(".controls").remove();

                    // appends the body content
                    parent.find(".content_body table").append(parent.find(".content_temp table"));
                } else {
                    // appends corner content
                    var cornerContent = parent.find(".content_temp tr:first-child td").eq(0).text();
                    parent.find(".content_corner table").append("<tr><td>" + cornerContent + "</td></tr>");

                    // appends top content
                    var topContent;
                    var topLocation = parent.find(".content_top table");
                    topLocation.append("<tr></tr>");
                    for (i = 1; i < parent.find(".content_temp tr:first-child td").length; i++) {
                        topContent = parent.find(".content_temp tr:first-child td").eq(i).text();
                        topLocation.find("tr").append("<td>" + topContent + "</td>");
                    }
                    var columns = parent.find(".content_top table td").length;
                    if (columns == 1) {
                        parent.find(".content_top table td").eq(0).addClass("first last");
                    } else if (columns <= tableColumns) {
                        parent.find(".content_top table td").eq(0).addClass("first");
                        parent.find(".content_top table td").eq(columns - 1).addClass("last");
                    } else {
                        parent.find(".content_top table td").eq(0).addClass("first");
                        parent.find(".content_top table td").eq(tableColumns - 1).addClass("last");
                    }

                    // appends left content
                    var leftContent;
                    var leftLocation = parent.find(".content_left table");
                    for (i = 1; i < parent.find(".content_temp tr").length; i++) {
                        leftContent = parent.find(".content_temp tr").eq(i).children("td").eq(0).text();
                        leftLocation.append("<tr><td>" + leftContent + "</td></tr>");
                    }

                    // appends the body content
                    bodyLocation = parent.find(".content_body table");
                    for (i = 1; i < parent.find(".content_temp tr").length; i++) {
                        bodyLocation.append("<tr></tr>");
                        for (j = 1; j < parent.find(".content_temp tr:first-child td").length; j++) {
                            bodyContent = parent.find(".content_temp tr").eq(i).children("td").eq(j).text();
                            bodyLocation.find("tr").eq(i - 1).append("<td>" + bodyContent + "</td>");
                        }
                    }

                    // sets buttons (for mobile)
                    var buttons = Math.ceil(columns / tableColumns);
                    if (buttons <= 1) {
                        parent.find(".controls").hide();
                    } else {
                        var carousel = parent.find(".carousel-indicators");
                        carousel.append("<li class='active'></li>");
                        for (i = 0; i < buttons - 1; i++) {
                            carousel.append("<li></li>");
                        }
                    }
                }
            }

            if (parent.closest(".campaign-card").length > 0) {
                parent.find(".window").addClass("right-0--desktop-start");
            } else {
                parent.find(".window").addClass("right-0--tablet-start");
            }

            //removes temporary content
            parent.find(".content_temp").remove();
        });
    }

    // Resize Rate Table
    function resizeRateTable() {
        $(".rate_table").each(function () {
            var parent = $(this);
            if (!parent.parent().hasClass("two-column-table")) {
                // Set column widths
                if (UtilityModule.getViewport() == "mobile" && !parent.hasClass("table-mobile")) {
                    mobileColumns(parent);
                    parent.removeClass("table-mobile table-tablet table-desktop");
                    parent.addClass("table-mobile");
                } else if (UtilityModule.getViewport() == "tablet" && !parent.hasClass("table-tablet")) {
                    if (parent.closest(".campaign-card").length > 0) {
                        mobileColumns(parent);
                    } else {
                        desktopColumns(parent);
                    }
                    parent.removeClass("table-mobile table-tablet table-desktop");
                    parent.addClass("table-tablet");
                } else if (UtilityModule.getViewport() == "desktop" && !parent.hasClass("table-desktop")) {
                    desktopColumns(parent);
                    parent.removeClass("table-mobile table-tablet table-desktop");
                    parent.addClass("table-desktop");
                }

                // Vertical height
                var content_left = parent.find(".content_left");
                if (content_left.height() >= content_left.find(".content_container").height()) {
                    content_left.css("padding-bottom", "0px");
                }

                // Fix widths for scrollbar
                var bodyWidth = parent.find(".content_body")[0].style.width;
                var topWidth = parseFloat(parent.find(".content_body").width()) - parseFloat(parent.find(".content_body .content_container").width());

                if (topWidth > 5) {
                    parent.find(".content_top").css("width", "calc(" + bodyWidth + " - " + topWidth + "px)");
                } else {
                    parent.find(".content_top").css("width", bodyWidth);
                }

                // Set heights for all elements
                tableRowHeight(parent, "content_corner", "content_top");
                tableRowHeight(parent, "content_left", "content_body");
            }
        });

        $(".comparison-table-layout").each(function () {
            var elements = $(this).find(".comparison-table h2");
            if (UtilityModule.getViewport() != "mobile") {
                var height = 0;
                elements.css("min-height", "0px");
                elements.each(function () {
                    height = $(this).outerHeight() > height ? $(this).outerHeight() : height;
                });
                elements.css("min-height", height + "px");
            } else {
                elements.css("min-height", "");
            }
        });
    }

    // Set column width for mobile
    function mobileColumns(parent) {
        var columns = parent.find(".content_top tr td").length;

        var visible;
        if (columns >= tableColumns) {
            visible = tableColumns;
        } else {
            visible = columns;
        }

        parent.find(".content_corner, .content_top, .content_left, .content_body").removeAttr("style");

        parent.find(".content_corner, .content_left").css("width", 1 / (visible + 1) * 100 + "%");
        parent.find(".content_top, .content_body").css("width", visible / (visible + 1) * 100 + "%");

        parent.find(".window").css("width", columns / visible * 100 + "%");
        parent.find("td").css("width", 1 / columns * 100 + "%");
    }

    // Set column width for desktop
    function desktopColumns(parent) {
        var columns = parent.find(".content_top tr td").length;
        var height = parseInt(parent.find(".content_left").css("max-height"));
        var width;

        var max_columns;
        if (parent.parent().hasClass("comparison-table")) {
            max_columns = 4;
        } else {
            max_columns = 10;
        }

        parent.find(".content_corner, .content_top, .content_left, .content_body").removeAttr("style");
        if ((columns + 1) > max_columns) {
            width = 100 / max_columns;
            parent.find(".content_corner, .content_left").css("width", width + "%");
            parent.find(".content_top, .content_body").css("width", width * (max_columns - 1) + "%");

            parent.find(".window").css("width", columns / (max_columns - 1) * 100 + "%");
            parent.find("td").css("width", 1 / columns * 100 + "%");

            parent.find(".content_left, .content_body").css("max-height", height + "px");
        } else {
            width = 100 / (columns + 1);
            parent.find(".content_corner, .content_left").css("width", width + "%");
            parent.find(".content_top, .content_body").css("width", width * columns + "%");

            parent.find(".window").css("width", 100 + "%");
            parent.find("td").css("width", 1 / columns * 100 + "%");
        }
    }

    // Set heights for all elements
    function tableRowHeight(parent, left, right) {
        var left_column = parent.find("." + left + " tr");
        var right_column = parent.find("." + right + " tr");

        left_column.each(function (index) {
            var tableRowLeft = left_column.eq(index);
            var tableRowRight = right_column.eq(index);

            var tableRowHeight;
            if (parent.parent().hasClass("policy-table")) {
                tableRowHeight = 50;
            } else {
                tableRowHeight = 0;
            }

            tableRowLeft.find("td").css('height', '0px');
            tableRowRight.find("td").css('height', '0px');
            tableRowHeight = tableRowLeft.outerHeight() > tableRowHeight ? tableRowLeft.outerHeight() : tableRowHeight;
            tableRowHeight = tableRowRight.outerHeight() > tableRowHeight ? tableRowRight.outerHeight() : tableRowHeight;

            tableRowLeft.find("td").css('height', tableRowHeight + 'px');
            tableRowRight.find("td").css('height', tableRowHeight + 'px');
        });
    }

    // Table swipe for mobile
    function tableSwipe(event, direction, distance, duration, fingerCount, fingerData) {
        var parent = $(this).closest(".rate_table");
        if (UtilityModule.getViewport() == "mobile") {
            var number, indicator, first, last;
            if (direction == "left") {
                if (!parent.find(".controls ol li").last().hasClass("active") && $('.controls').is(':visible')) {
                    number = parent.find("td.last").nextAll().length;
                    if (number >= tableColumns) {
                        parent.find('.window').animate({right: '+=100%'}, "slow");
                        number = tableColumns;
                    }
                    else {
                        parent.find('.window').animate({right: '+=' + 100 / tableColumns * number + '%'}, "slow");
                    }

                    indicator = parent.find("ol li.active");
                    indicator.removeClass("active");
                    indicator.next().addClass("active");

                    first = parent.find("td.first");
                    last = parent.find("td.last");
                    first.removeClass("first");
                    first.nextAll().eq(number - 1).addClass("first");
                    last.removeClass("last");
                    last.nextAll().eq(number - 1).addClass("last");
                }
            } else {
                if (!parent.find(".controls ol li").first().hasClass("active") && $('.controls').is(':visible')) {
                    number = parent.find("td.first").prevAll().length;
                    if (number >= tableColumns) {
                        parent.find('.window').animate({right: '-=100%'}, "slow");
                        number = tableColumns;
                    }
                    else {
                        parent.find('.window').animate({right: '-=' + 100 / tableColumns * number + '%'}, "slow");
                    }

                    indicator = parent.find("ol li.active");
                    indicator.removeClass("active");
                    indicator.prev().addClass("active");

                    first = parent.find("td.first");
                    last = parent.find("td.last");
                    first.removeClass("first");
                    first.prevAll().eq(number - 1).addClass("first");
                    last.removeClass("last");
                    last.prevAll().eq(number - 1).addClass("last");
                }
            }
        }
    }

    return {
        init: function () {
            formatRateTable();
            resizeRateTable();

            $(window).on("resize", function () {
                resizeRateTable();
            });

            // Swipe to Next/Previous Set of Columns
            $('.rate_table .content_body').swipe({
                preventDefaultEvents: false,
                swipeLeft: tableSwipe,
                swipeRight: tableSwipe
            });

            // Scrolling for Rate Table
            $('.rate_table .content_body').on("scroll", function () {
                var parent = $(this).closest(".rate_table");
                parent.find(".content_top").scrollLeft($(this).scrollLeft());
                parent.find(".content_left").scrollTop($(this).scrollTop());
            });
        },
        resizeRateTable: resizeRateTable
    };
})();
GenericTableModule.init();

var InputTypeAhead = (function () {
    var typeAheadFields = {};

    //helper to change all strings in array to lower
    function keysToLowerCase(myArr) {
        return myArr.join('|').toLowerCase().split('|');
    }

    function substringMatcher(strs) {
        return function findMatches(q, cb) {
            var matches, substringRegex;

            // an array that will be populated with substring matches
            matches = [];

            // regex used to determine if a string contains the substring `q`
            substrRegex = new RegExp(q, 'i');

            // iterate through the pool of strings and for any string that
            // contains the substring `q`, add it to the `matches` array
            $.each(strs, function (i, str) {
                if (substrRegex.test(str)) {
                    matches.push(str);
                }
            });

            cb(matches);
        };
    }

    //read in json file
    //return inputJson - json object with list (needs to match file name)
    //       fileName - unsure of another way to do this, read in json object name e.g. districts
    function readJsonFromPath(inputToReadIn) {
        var inputJson = [];
        var urlToRead = $(inputToReadIn).data('json-path');
        var lastSlash = urlToRead.lastIndexOf("/");
        var fileExtension = urlToRead.lastIndexOf(".");
        var fileName = urlToRead.slice(lastSlash + 1, fileExtension);

        $.getJSON(urlToRead, function (json) {
            for (var i = 0; i < json[0][fileName].length; i++) {
                inputJson.push(json[0][fileName][i]);
            }

            typeAheadFields[urlToRead] = {};
            typeAheadFields[urlToRead].fileName = fileName;
            typeAheadFields[urlToRead].inputJson = inputJson;
        });

        return {
            inputJson: inputJson,
            fileName: fileName
        };
    }

    //return true if input box value is included in json list
    function validateInListObject(input, currentVal) {
        var containsKey = false;
        var urlToRead = $(input).data('json-path');
        var inputJson = typeAheadFields[urlToRead].inputJson;
        containsKey = keysToLowerCase(inputJson).indexOf(currentVal.toLowerCase()) > -1;
        return containsKey;
    }

    //use twitter typeahead to turn input boxes into input box + hidden dropdown of populated list from json
    return {
        //use twitter typeahead to turn input boxes into input box + hidden dropdown of populated list from json
        init: function () {
            $('.js-input-typeahead').each(function () {
                var readObject = readJsonFromPath($(this));
                var inputJson = readObject.inputJson;
                var fileName = readObject.fileName;

                $(this).typeahead({
                        hint: false,
                        highlight: true,
                        minLength: 1
                    },
                    {
                        name: fileName,
                        source: substringMatcher(inputJson)
                    });
            });
        },
        validateInListObject: validateInListObject
    };
})();
InputTypeAhead.init();
