if ($(".microsite-product-chart").length > 0) {
    var MicrositeComparisonChartModule = (function () {
        var comparisonChartAll = $(".microsite-product-chart");
        var comparisonChartID = "micrositeComparisonChartCarousel";

        function comparisonChartUniqueID() {
            comparisonChartAll.each(function (index) {
                var comparisonChart = $(this);
                comparisonChart.find(".carousel").attr("id", comparisonChartID + index);
                comparisonChart.find(".carousel-control").attr("href", "#" + comparisonChartID + index);
            });
        }

        function comparisonChartInitialize() {
            comparisonChartAll.each(function (index) {
                var comparisonChart = $(this);
                var comparisonChartButtons = comparisonChart.find(".life_chart_learn");

                if (comparisonChartButtons.children().length == 0) {
                    comparisonChartButtons.remove();
                    comparisonChart.find(".product_chart").addClass("product_chart--no-buttons");
                } else {
                    comparisonChartButtons.each(function () {
                        if ($(this).children().length == 0) {
                            $(this).css("padding", "0");
                        }
                    });
                }
            });
        }

        function comparisonChartResize() {
            comparisonChartAll.each(function () {
                var comparisonChart = $(this);

                if (UtilityModule.getViewport() == "mobile") {
                    comparisonChart.find(".carousel .carousel-inner .carousel-item").css("width", "100%");
                    comparisonChart.find(".carousel .carousel-inner .carousel-item .column-wrapper").css("width", "100%");
                } else {
                    //total number of column wrappers
                    var columnWrapperNumTotal = comparisonChart.find(".carousel .carousel-item").find('.column-wrapper').length;
                    comparisonChart.find(".carousel .carousel-inner .carousel-item").not('.visible-xs').each(function () {
                        //number of column wrapper in a single carousel-item
                        var columnWrapperNum = $(this).find('.column-wrapper').not('.visible-xs').length;
                        $(this).css('width', columnWrapperNum / columnWrapperNumTotal * 100 + '%');
                        $(this).find('.column-wrapper').css('width', 100 / columnWrapperNum + '%');
                    });
                }

                comparisonChart.find(".carousel .carousel-inner .carousel-item .column-wrapper").each(function (index) {
                    if (index % 2 == 0) {
                        $(this).addClass("column--odd");
                    } else {
                        $(this).addClass("column--even");
                    }
                });

                // Init first block
                comparisonChart.find('.carousel-item').removeClass('selected');
                comparisonChart.find('.carousel-item').first().addClass('selected');

                // Determine how many blocks to show based on screen width
                var slides = comparisonChart.find(".carousel-inner [class*='col-']");

                var splitter = 0;
                if (UtilityModule.getViewport() == "mobile") {
                    splitter = 1;
                } else {
                    splitter = 5;
                }

                comparisonChart.find('.item [class*=col-]').removeClass('active').unwrap();

                for (var i = 0; i < slides.length; i += splitter) {
                    slides.slice(i, i + splitter).wrapAll("<div class='item'></div>");
                }

                comparisonChart.find('.item').first().addClass('active');
            });
        }

        function comparisonChartHeaderConfig() {
            comparisonChartAll.each(function () {
                var comparisonChart = $(this);
                var carouselItems = comparisonChart.find(".carousel .item .carousel-item");
                var totalItems = carouselItems.length;
                var group1 = 1;
                var group2 = 0;

                if (UtilityModule.getViewport() == "mobile") {
                    carouselItems.find(".microsite-column-category").css("width", "100%");
                } else {
                    var i;
                    var startingPoint = carouselItems.eq(0).find(".microsite-column-category").text().trim();
                    for (i = 1; i < totalItems; i++) {
                        if (carouselItems.eq(i).find(".microsite-column-category").text().trim() == startingPoint) {
                            group1 += 1;
                        }
                    }
                    group2 = totalItems - group1;

                    var group1Start = 0;
                    for (i = group1Start; i < group2; i++) {
                        carouselItems.eq(i).find(".microsite-column-category").css("width", group1 * 100 + "%");
                    }
                    carouselItems.eq(group1Start).find(".microsite-column-category").css("visibility", "visible");

                    var group2Start = totalItems - group2;
                    for (i = group2Start; i < totalItems; i++) {
                        carouselItems.eq(i).find(".microsite-column-category").css("width", group2 * 100 + "%");
                    }
                    carouselItems.eq(group2Start).find(".microsite-column-category").css("visibility", "visible");
                }
            });
        }

        function comparisonChartMatchHeights() {
            comparisonChartAll.each(function () {
                var comparisonChart = $(this);
                var elements, height;

                // top cells
                if (UtilityModule.getViewport() == "mobile") {
                    elements = comparisonChart.find(".microsite-column-category").not(":hidden");
                    UtilityModule.heightMatcher(elements);
                } else {
                    elements = comparisonChart.find(".microsite-column-category").filter(function () {
                        return $(this).css("visibility") != 'hidden';
                    });

                    height = 0;
                    elements.css('min-height', '0px');
                    elements.each(function () {
                        height = $(this).height() > height ? $(this).height() : height;
                    });

                    comparisonChart.find(".microsite-column-category").css('min-height', height + 'px');
                    comparisonChart.find(".left-col-mcc").css('min-height', height + 'px');
                }

                // top cells
                var leftColumn = comparisonChart.find(".life_insurance_col1 .column-cell");
                var rightColumns = comparisonChart.find(".carousel-item");
                leftColumn.each(function (index) {
                    elements = $(this);
                    rightColumns.each(function () {
                        elements = elements.add($(this).find(".column-cell").eq(index));
                    });
                    UtilityModule.heightMatcher(elements);
                });

                // bottom cells
                elements = comparisonChart.find(".carousel-item .life_chart_learn");
                UtilityModule.heightMatcher(elements);
            });
        }

        return {
            init: function () {
                comparisonChartUniqueID();
                comparisonChartInitialize();

                $(window).on("load resize", function () {
                    comparisonChartResize();
                    comparisonChartHeaderConfig();
                    comparisonChartMatchHeights();
                });

                comparisonChartAll.find(".carousel").on('slid.bs.carousel', function () {
                    comparisonChartMatchHeights();
                    var nextIndex = $(this).find('.item.active > .carousel-item').attr('data-target');
                    $(this).find('.carousel-item').eq(nextIndex).addClass('selected');
                });
            }
        };
    })();
    MicrositeComparisonChartModule.init();
}
