if ($(".agi-contacts").length > 0) {
    var MicrositesAdvancedAgiContactsModule = (function () {
        var agiParentObj = $(".agi-content-container");

        //reset the error message and result show container
        function resetAgiContactsContainer() {
            $(".agi-result-container").remove();
            $('.errorMsgtext_no_contact_found').addClass('hidden');
        }

        //If user on desktop, disable Tel dialing
        function enableTelLinkMobile() {
            if (UtilityModule.getViewport() != "mobile") {
                $("a[href*='tel:']").each(function () {
                    $(this).removeAttr('href').addClass('disabled-anchor');
                });
            }
            else {
                $("a.agi-contact-profile_tel").each(function () {
                    var telNum = $(this).data("tel");
                    $(this).attr('href', "tel:" + telNum).removeClass('disabled-anchor');
                });
            }
        }

        //display country dropdown
        function agiCountryResults(response) {
            var countryList = response.data;
            var countrySelector = agiParentObj.find(".agi-contacts-country-selector select");
            if (countryList.length > 0) {
                countrySelector.find('option').not(':first').remove();
                for (var i = 0; i < countryList.length; i++) {
                    var currentCountryData = countryList[i];
                    var optionList = $("<option value=\"" + currentCountryData.countryValue + "\">" + currentCountryData.countryName + "</option>").data('currentCountryData', currentCountryData);
                    countrySelector.append(optionList);
                }
            }
        }

        // Generate the markup for the agi contacts results
        function agiContactsResults(response) {
            resetAgiContactsContainer();
            var contactsListData = response.data;
            var resultsListHTML = "";
            var contactResultAriaLabel = $(".contact_result_aria_label").text();
            resultsListHTML += "<div class=\"agi-result-container\" role=\"region\" aria-label=\"" + contactResultAriaLabel + "\">";
            for (var i = 0; i < contactsListData.length; i++) {
                var contactHeaderData = contactsListData[i];
                var contactList = contactHeaderData.contactList;
                var label_tel_one = $(".label_tel_one").text();
                var label_tel_two = $(".label_tel_two").text();
                var label_fax = $(".label_fax").text();

                resultsListHTML += "<div class=\"agi-contact-profile-container\">";
                if (contactHeaderData.contactHeaderTitle) {
                    resultsListHTML += "<h3>" + contactHeaderData.contactHeaderTitle + ":</h3>";
                }
                for (var j = 0; j < contactList.length; j++) {
                    var contactData = contactList[j];
                    resultsListHTML += "<div class=\"row\">";
                    resultsListHTML += "<div class=\"col-md-6 col-sm-6 col-xs-6\">";
                    if (contactData.name) {
                        resultsListHTML += "<p class=\"agi-contact-profile_name\">" + contactData.name + "</p>";
                    }
                    if (contactData.designation) {
                        resultsListHTML += "<p>" + contactData.designation + "</p>";
                    }
                    if (contactData.address1) {
                        resultsListHTML += "<p>" + contactData.address1 + "</p>";
                    }
                    if (contactData.address2) {
                        resultsListHTML += "<p>" + contactData.address2 + "</p>";
                    }
                    if (contactData.city || contactData.state || contactData.zipcode) {
                        resultsListHTML += "<p>";
                        if (contactData.city) {
                            resultsListHTML += "<span>" + contactData.city + ", </span>";
                        }
                        if (contactData.state) {
                            resultsListHTML += "<span>" + contactData.state + " </span>";
                        }
                        if (contactData.zipcode) {
                            resultsListHTML += "<span>" + contactData.zipcode + "</span>";
                        }
                        resultsListHTML += "</p>";
                    }
                    if (contactData.email) {
                        resultsListHTML += "<p><a href='mailto:" + contactData.email + "'>" + contactData.email + "</a></p>";
                    }
                    resultsListHTML += "</div>";
                    if (contactData.tel1 || contactData.tel2 || contactData.fax) {
                        resultsListHTML += "<div class=\"col-md-6 col-sm-6 col-xs-6 text-right\">";
                        if (contactData.tel1) {
                            resultsListHTML += "<p>" + label_tel_one + ": <a href=\"tel:" + contactData.tel1 + "\" class=\"agi-contact-profile_tel\" data-tel=\"" + contactData.tel1 + "\">" + contactData.tel1 + "</a></p>";
                        }
                        if (contactData.tel2) {
                            resultsListHTML += "<p>" + label_tel_two + ": <a href=\"tel:" + contactData.tel2 + "\" class=\"agi-contact-profile_tel\" data-tel=\"" + contactData.tel2 + "\">" + contactData.tel2 + "</a></p>";
                        }
                        if (contactData.fax) {
                            resultsListHTML += "<p>" + label_fax + ": <span class=\"agi-contact-profile_fax\">" + contactData.fax + "</span></p>";
                        }
                        resultsListHTML += "</div>";
                    }
                    resultsListHTML += "</div>";
                }
                resultsListHTML += "</div>";
            }
            resultsListHTML += "</div>";
            $(resultsListHTML).insertAfter(".agi-content-header");
        }

        // ajax call for getting response ..
        function agiContactsServiceCall(responseType, countryValue, stateValue) {
            var serviceUrl = $("[data-agi-url]").attr("data-agi-url");
            var dataStorePath = $("[data-agi-storepath]").attr("data-agi-storepath");
            var postData = {};
            postData.countryName = countryValue ? countryValue : "";
            postData.regionName = stateValue ? stateValue : "";
            postData.dataStorePath = dataStorePath ? dataStorePath : "";


            $.ajax({
                url: serviceUrl,
                type: 'GET',
                data: postData,
                dataType: 'json',
                async: true,
                success: function (response) {
                    if (response.status == "success") {
                        if (responseType == "contactsList") {
                            agiContactsResults(response);
                            enableTelLinkMobile();
                        }
                        else {
                            agiCountryResults(response);
                        }
                    }
                    else {
                        $('.errorMsgtext_no_contact_found').removeClass('hidden');
                    }
                },
                error: function () {
                    console.log("error in ajax form submission");
                }
            });
        }

        return {
            init: function () {
                agiContactsServiceCall();

                $(window).on("resize", function () {
                    enableTelLinkMobile();
                });

                //get states list once user select country option
                $(".agi-contacts-country-selector select").on('change', function () {
                    resetAgiContactsContainer();
                    var currentOption = $(this).find("option:selected");
                    var countryValue = $(this).val();
                    var stateSelector = agiParentObj.find(".agi-contacts-state-selector select");
                    stateSelector.find('option').not(':first').remove();
                    if (countryValue != "") {
                        var currentStateList = currentOption.data('currentCountryData');
                        var statesList = currentStateList.statesList;
                        if (statesList.length > 0) {
                            for (var i = 0; i < statesList.length; i++) {
                                var currentState = statesList[i];
                                var optionList = $("<option value=\"" + currentState.stateValue + "\">" + currentState.stateName + "</option>").data('currentCountry', currentStateList.countryValue);
                                stateSelector.append(optionList);
                            }
                            stateSelector.prop("disabled", false);
                        }
                        else {
                            agiContactsServiceCall("contactsList", countryValue, "");
                            stateSelector.prop("disabled", true);
                        }
                    }
                    else {
                        stateSelector.prop("disabled", true);
                    }
                });

                //get contacts list once user select state option
                $(".agi-contacts-state-selector select").on('change', function () {
                    resetAgiContactsContainer();
                    var currentOption = $(this).find("option:selected");
                    var stateValue = $(this).val();
                    if (stateValue != "") {
                        var currentCountry = currentOption.data("currentCountry");
                        agiContactsServiceCall("contactsList", currentCountry, stateValue);
                    }
                });
            }
        };
    })();
    MicrositesAdvancedAgiContactsModule.init();
}
