if ($(".microsites-advanced-login-container").length > 0) {
    var MicrositesAdvancedHeaderLoginModule = (function () {
        function submitLoginForm(form) {
            var valid = true;
            var username = $('.login-type-username').find('input');
            var password = $('.login-type-password').find('input');
            var AlphaNumericRegex = new RegExp(/^[a-zA-Z0-9]*$/);

            //Remove with PLACEHOLDER
            $('.login-popout').find("input").each(function () {
                var currentInputObj = $(this);
                if (currentInputObj.attr("placeholder") == currentInputObj.val()) {
                    currentInputObj.val("");
                }
            });
            if (username.val() == "") {
                username.addClass('error');
                valid = false;
            } else {
                if (!(AlphaNumericRegex.test(username.val()))) {
                    username.addClass('error');
                    valid = false;
                }
            }
            if (password.val() == "") {
                password.addClass('error');
                valid = false;
            } else {
                if (!(AlphaNumericRegex.test(password.val()))) {
                    password.addClass('error');
                    valid = false;
                }
            }
            if (valid) {
                form.submit();
            } else {
                return false;
            }
        }

        return {
            init: function () {
                // Accessibility : adding screen reader text for links that open a new tab or window
                $('.microsites-advanced-login-container a').each(function () {
                    var currentAnchor = $(this);
                    var targetLink = currentAnchor.attr("target");
                    var srText = $('.microsites-advanced-login-container form').attr('data-srText');
                    if (targetLink === "_blank") {
                        currentAnchor.append('<span class=\"sr-only\">' + srText + '</span>');
                    }
                });

                // Accessibility : keeping focus inside overlay
                $('.microsites-advanced-header .login-trigger').on("click", function (e) {
                    var inputs = $('.microsites-advanced-login-container').find('select, input, textarea, button, a').filter(':visible');
                    var lastPageElement = inputs.last();
                    lastPageElement.keydown(function (e) {
                        if (e.keyCode === 9) {
                            if (e.shiftKey) {
                                inputs.is(":nth-last-child(2)").focus();
                            } else {
                                e.preventDefault();
                                $(".microsites-advanced-login-container .login-container--close").focus();
                            }
                        }
                    });
                });

                //Accessibility : close login form on press of enter key on div and attributes for screen reader
                $('.microsites-advanced-login-container .login-container--close').keydown(function (e) {
                    if (e.keyCode === 13) {
                        $(this).click();
                        $('.microsites-advanced-header .login-trigger__label').attr('aria-expanded', 'false').focus();
                    }
                });

                //form submission
                $('#micrositesAdvancedFormLogin').on("submit", function (e) {
                    e.preventDefault();
                    submitLoginForm(this);
                });
            }
        };
    })();
    MicrositesAdvancedHeaderLoginModule.init();
}
